// LineChart.js
import React from 'react';
import Chart from 'react-apexcharts';

const convertToHoursAndMinutes = (minutes) => {
    const hours = Math.floor(minutes / 60);
    const mins = minutes % 60;
    return `${hours} hours ${mins} minutes`;
};

const convertToHoursAndMinutes1 = (minutes) => {
    const hours = Math.floor(minutes / 60);
    return `${hours} hours`;
};

const LineChart = ({ data, title, xaxisTitle, yaxisTitle, showTotalPoint }) => {
    const totalValue = data.reduce((acc, item) => acc + item.value, 0);
    const chartData = [...data];

    if (showTotalPoint) {
        chartData.push({ date: 'Total', value: totalValue });
    }

    const chartOptions = {
        chart: {
            type: 'line'
        },
        xaxis: {
            categories: chartData.map(item => item.date),
            title: {
                text: xaxisTitle
            }
        },
        yaxis: {
            title: {
                text: yaxisTitle
            },
            labels: {
                formatter: (value) => convertToHoursAndMinutes1(value)
            }
        },
        title: {
            text: title
        },
        stroke: {
            curve: 'smooth'
        },
        dataLabels: {
            enabled: false
        },
        markers: {
            size: 5
        },
        series: [
            {
                name: title,
                data: chartData.map(item => item.value)
            }
        ],
        tooltip: {
            y: {
                formatter: (value) => convertToHoursAndMinutes(value)
            }
        },
        colors: ['#00E396', '#FF4560', '#775DD0']
    };

    return (
        <div>
            <Chart options={chartOptions} series={chartOptions.series} type="line" height="350" />
        </div>
    );
};

export default LineChart;
