import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Overview.css';

const Overview = () => {
  const [employeesData, setEmployeesData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const cleanFieldNames = (data) => {
    return data.map((item) => {
      const cleanedItem = {};
      for (const [key, value] of Object.entries(item)) {
        const cleanedKey = key.replace(/^\uFEFF/, ''); // Remove BOM if present
        cleanedItem[cleanedKey] = value;
      }
      return cleanedItem;
    });
  };

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [employeeResponse, timesheetResponse, companyDetailsResponse, modulesResponse] = await Promise.all([
          axios.get('http://localhost:5001/Employee_Data1'),
          axios.get('http://localhost:5001/timesheet'),
          axios.get('http://localhost:5001/CompanyDetails'),
          axios.get('http://localhost:5001/Modules_Tasks')
        ]);

        const employeeData = employeeResponse.data;
        const timesheetData = timesheetResponse.data;
        const rawCompanyDetailsData = companyDetailsResponse.data;
        const moduleData = modulesResponse.data;

        // Clean the company details data
        const companyDetailsData = cleanFieldNames(rawCompanyDetailsData);

        // Process unique user_id and project pairs with tasks
        const userProjectCompanyMap = new Map();
        timesheetData.forEach((entry) => {
          const { User_Id, project, task } = entry;
          const company = companyDetailsData.find(
            (company) => company.company_id === project
          );
          const taskModule = moduleData.find(
            (module) => module['m_slno'] === task
          );

          if (company && taskModule) {
            const key = `${User_Id}-${task}`; // Treat user_id and task as unique key
            if (!userProjectCompanyMap.has(key)) {
              userProjectCompanyMap.set(key, {
                userId: User_Id,
                projects: new Set(),
                tasks: new Set(),
              });
            }
            const userEntry = userProjectCompanyMap.get(key);
            userEntry.projects.add(company.company_name);
            userEntry.tasks.add(taskModule.mod_name);
          }
        });

        // Process employee data for display in table
        const processedData = employeeData.map((employee) => {
          const relevantEntries = Array.from(userProjectCompanyMap.values()).filter(
            (entry) => entry.userId === employee.Emp_ID
          );

          const projectNamesArray = relevantEntries.flatMap((entry) => [...entry.projects]);
          const taskNamesArray = relevantEntries.flatMap((entry) => [...entry.tasks]);

          // Create formatted project names with two per line and commas handled correctly
          const formattedProjects = projectNamesArray.length > 0
            ? projectNamesArray.reduce((acc, project, index) => {
                if (index > 0 && index % 2 === 0) {
                  acc.push(<br key={`br-${index}`} />); // Add a line break after every two projects
                }
                acc.push(
                  <span key={index} className="project-cell">
                    {project}
                    {index !== projectNamesArray.length - 1 ? ', ' : ''}
                  </span>
                );
                return acc;
              }, [])
            : 'No project assigned';

          const formattedTasks = taskNamesArray.length > 0
            ? taskNamesArray.join(', ')
            : 'No tasks assigned';

          return {
            name: `${employee.User_Fname} ${employee.User_Lname}` || 'N/A',
            email: employee.User_Email || 'N/A',
            status: employee.User_Status || 'N/A',
            companyName: formattedProjects,
            tasks: formattedTasks,
          };
        });

        setEmployeesData(processedData);
      } catch (error) {
        setError('An error occurred while fetching employee data.');
      } finally {
        setLoading(false);
      }
    };

    fetchData();
    const interval = setInterval(fetchData, 2000);
    return () => clearInterval(interval);

  }, []);

  return (
    <div className="Overview-container">
      <h1 className="title">Employee Time Sheet</h1>
      <div className="EmployeeTable">
        {loading && !error ? (
          <p className="loading-text">Loading...</p>
        ) : error ? (
          <p className="error-text">{error}</p>
        ) : (
          <div className="table-container">
            <table className="table">
              <thead>
                <tr>
                  <th>Employee Name</th>
                  <th>Email</th>
                  <th>Status</th>
                  <th>Project</th>
                  <th>Task</th>
                </tr>
              </thead>
              <tbody>
                {employeesData.map((employee, index) => (
                  <tr key={index}>
                    <td>{employee.name}</td>
                    <td>{employee.email}</td>
                    <td>{employee.status}</td>
                    <td>{employee.companyName}</td>
                    <td>{employee.tasks}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
};

export default Overview;
