import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { PieChart, Pie, Cell, Legend, Tooltip } from 'recharts';

const Overview = () => {
  const [data, setData] = useState([]);
  const [employeeCount, setEmployeeCount] = useState(0);
  const [statusCount, setStatusCount] = useState({});
  const [departmentCount, setDepartmentCount] = useState({});
  const [totalHours, setTotalHours] = useState(0);

  useEffect(() => {
    axios.get('http://localhost:5001/data')
      .then(response => {
        const data = response.data;
        setData(data);

        // Calculate total number of unique employees
        const uniqueEmployees = new Set(data.map(item => item.emp_id));
        setEmployeeCount(uniqueEmployees.size);

        // Calculate status distribution
        const statusDist = data.reduce((acc, item) => {
          acc[item.status] = (acc[item.status] || 0) + 1;
          return acc;
        }, {});
        setStatusCount(statusDist);

        // Calculate department distribution
        const departmentDist = data.reduce((acc, item) => {
          acc[item.department] = (acc[item.department] || 0) + 1;
          return acc;
        }, {});
        setDepartmentCount(departmentDist);

        // Calculate total hours worked
        const total = data.reduce((acc, item) => acc + parseFloat(item.total_hrs), 0);
        setTotalHours(total);
      })
      .catch(error => console.error('Error fetching data:', error));
  }, []);

  const statusData = Object.keys(statusCount).map(status => ({
    name: status,
    value: statusCount[status]
  }));

  const departmentData = Object.keys(departmentCount).map(department => ({
    name: department,
    value: departmentCount[department]
  }));

  return (
    <div>
      <h2>Overview</h2>
      <div>Total Number of Employees: {employeeCount}</div>
      <h3>Status Distribution</h3>
      <PieChart width={400} height={400}>
        <Pie
          data={statusData}
          dataKey="value"
          nameKey="name"
          cx="50%"
          cy="50%"
          outerRadius={150}
          fill="#8884d8"
          label
        >
          {statusData.map((entry, index) => (
            <Cell key={`cell-${index}`} fill={['#0088FE', '#00C49F', '#FFBB28'][index % 3]} />
          ))}
        </Pie>
        <Tooltip />
        <Legend />
      </PieChart>
      <h3>Department Distribution</h3>
      <PieChart width={400} height={400}>
        <Pie
          data={departmentData}
          dataKey="value"
          nameKey="name"
          cx="50%"
          cy="50%"
          outerRadius={150}
          fill="#82ca9d"
          label
        >
          {departmentData.map((entry, index) => (
            <Cell key={`cell-${index}`} fill={['#0088FE', '#00C49F', '#FFBB28'][index % 3]} />
          ))}
        </Pie>
        <Tooltip />
        <Legend />
      </PieChart>
      <div>Total Hours Worked: {totalHours}</div>
    </div>
  );
};

export default Overview;
