import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { PieChart, Pie, Cell, Tooltip, Legend } from 'recharts';

function Overview() {
  const [data, setData] = useState([]);
  const [statusCounts, setStatusCounts] = useState({ approved: 0, unapproved: 0, pending: 0 });
  const [totalHours, setTotalHours] = useState(0);

  useEffect(() => {
    axios.get('http://localhost:5000/data')
      .then(response => {
        setData(response.data);
        calculateStats(response.data);
      })
      .catch(error => {
        console.error('There was an error fetching the data!', error);
      });
  }, []);

  const calculateStats = (data) => {
    const counts = { approved: 0, unapproved: 0, pending: 0 };
    let totalHrs = 0;

    data.forEach(employee => {
      counts[employee.status.toLowerCase()] += 1;
      totalHrs += parseFloat(employee.total_hrs);
    });

    setStatusCounts(counts);
    setTotalHours(totalHrs);
  };

  const COLORS = ['#0088FE', '#00C49F', '#FFBB28'];

  const statusData = [
    { name: 'Approved', value: statusCounts.approved },
    { name: 'Unapproved', value: statusCounts.unapproved },
    { name: 'Pending', value: statusCounts.pending }
  ];

  return (
    <div className="overview">
      <h2>Overview</h2>
      <div className="stats">
        <h3>Total Employees: {data.length}</h3>
        <h3>Total Hours Worked: {totalHours.toFixed(2)}</h3>
      </div>
      <div className="charts">
        <PieChart width={400} height={400}>
          <Pie dataKey="value" isAnimationActive={false} data={statusData} cx={200} cy={200} outerRadius={80} fill="#8884d8" label>
            {statusData.map((entry, index) => <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />)}
          </Pie>
          <Tooltip />
          <Legend />
        </PieChart>
      </div>
    </div>
  );
}

export default Overview;
