import React, { useState, useEffect, useMemo } from 'react';
import { useTable } from '@tanstack/react-table';
import axios from 'axios';
import './Overview.css';

const Overview = () => {
  const [employeesData, setEmployeesData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [
          employeeResponse,
          timesheetResponse,
          companyDetailsResponse,
          moduleTaskResponse,
        ] = await Promise.all([
          axios.get('http://localhost:5001/Employee_Data1'),
          axios.get('http://localhost:5001/timesheet'),
          axios.get('http://localhost:5001/CompanyDetails'),
          axios.get('http://localhost:5001/Modules_Tasks'),
        ]);

        const employeeData = employeeResponse.data;
        const timesheetData = timesheetResponse.data;
        const rawCompanyDetailsData = companyDetailsResponse.data;
        const moduleTaskData = moduleTaskResponse.data;

        // Process data here as needed
        const processedData = employeeData.map((employee) => ({
          name: `${employee.User_Fname} ${employee.User_Lname}` || 'N/A',
          email: employee.User_Email || 'N/A',
          status: employee.User_Status || 'N/A',
          companyName: 'Sample Company', // Example data
          taskName: 'Sample Task', // Example data
        }));

        setEmployeesData(processedData);
      } catch (error) {
        setError('An error occurred while fetching employee data.');
      } finally {
        setLoading(false);
      }
    };

    fetchData();
    const interval = setInterval(fetchData, 2000);
    return () => clearInterval(interval);
  }, []);

  const columns = useMemo(
    () => [
      {
        Header: 'Employee Name',
        accessor: 'name',
      },
      {
        Header: 'Email',
        accessor: 'email',
      },
      {
        Header: 'Status',
        accessor: 'status',
      },
      {
        Header: 'Project',
        accessor: 'companyName',
      },
      {
        Header: 'Task',
        accessor: 'taskName',
      },
    ],
    []
  );

  const {
    getTableProps,
    getTableBodyProps,
    headerGroups,
    rows,
    prepareRow,
  } = useTable({
    columns,
    data: employeesData,
  });

  return (
    <div className="Overview-container">
      <h1 className="title">Employee Time Sheet</h1>
      <div className="EmployeeTable">
        {loading && !error ? (
          <p className="loading-text">Loading...</p>
        ) : error ? (
          <p className="error-text">{error}</p>
        ) : (
          <table {...getTableProps()} className="table">
            <thead>
              {headerGroups.map(headerGroup => (
                <tr {...headerGroup.getHeaderGroupProps()}>
                  {headerGroup.headers.map(column => (
                    <th {...column.getHeaderProps()}>{column.render('Header')}</th>
                  ))}
                </tr>
              ))}
            </thead>
            <tbody {...getTableBodyProps()}>
              {rows.map(row => {
                prepareRow(row);
                return (
                  <tr {...row.getRowProps()}>
                    {row.cells.map(cell => (
                      <td {...cell.getCellProps()}>{cell.render('Cell')}</td>
                    ))}
                  </tr>
                );
              })}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
};

export default Overview;
