import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Overview.css';

const Overview = () => {
    const [employeesData, setEmployeesData] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    // const cleanFieldNames = (data) => {
    //     return data.map(item => {
    //       const cleanedItem = {};
    //       for (const [key, value] of Object.entries(item)) {
    //         const cleanedKey = key.replace(/^\uFEFF/, ''); // Remove BOM if present
    //         cleanedItem[cleanedKey] = value;
    //       }
    //       return cleanedItem;
    //     });
    //   };

      useEffect(() => {
        const fetchEmployeeData = async () => {
          try {
            const [employeeResponse, timesheetResponse, companyDetailsResponse] = await Promise.all([
              axios.get('http://localhost:5001/Employee_Data1'),
              axios.get('http://localhost:5001/timesheet'),
              axios.get('http://localhost:5001/CompanyDetails'),
            ]);

            const employeeData = employeeResponse.data;
            const timesheetData = timesheetResponse.data;
            const rawCompanyDetailsData = companyDetailsResponse.data;

            // Clean the company details data
            const companyDetailsData = cleanFieldNames(rawCompanyDetailsData);

            // Process unique user_id and project pairs
            const userProjectPairs = new Set();
            timesheetData.forEach((entry) => {
              const { User_Id, project } = entry;
              const pair = `${User_Id}-${project}`;
              userProjectPairs.add(pair);
            });

            console.log("Unique User_Id and Project pairs:");
            userProjectPairs.forEach((pair) => {
              const [userId, project] = pair.split('-');
              console.log(`User_Id: ${userId}, Project: ${project}`);
            });

            // Process unique company_id and company_name pairs
            const uniqueCompanies = new Set();
            companyDetailsData.forEach((company) => {
              const { company_id, company_name } = company;
              const pair = `${company_id}-${company_name}`;
              uniqueCompanies.add(pair);
            });

            console.log("Unique Company_id and Company_name pairs:");
            uniqueCompanies.forEach((pair) => {
              const [companyId, companyName] = pair.split('-');
              console.log(`Company_id: ${companyId}, Company_name: ${companyName}`);
            });

            // Process employee data for display (if needed later)
            const processedData = employeeData.map((employee) => ({
              name: employee.User_Fname + " " + employee.User_Lname || 'N/A',
              email: employee.User_Email || 'N/A',
              status: employee.User_Status || 'N/A',
            }));

            setEmployeesData(processedData);
          } catch (error) {
            setError('An error occurred while fetching employee data.');
          } finally {
            setLoading(false);
          }
        };

        fetchEmployeeData();
      }, []);


    return (
        <div className="Overview-container">
            <h1 className="title">Employee Details</h1>
            <div className="EmployeeTable">
                <h2>Employees Details</h2>
                {loading && !error ? (
                    <p>Loading...</p>
                ) : error ? (
                    <p>{error}</p>
                ) : (
                    <table>
                        <thead>
                            <tr>
                                <th>Employee Name</th>
                                <th>Email</th>
                                <th>Status</th>
                            </tr>
                        </thead>
                        <tbody>
                            {employeesData.map((employee, index) => (
                                <tr key={index}>
                                    <td>{employee.name}</td>
                                    <td>{employee.email}</td>
                                    <td>{employee.status}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                )}
            </div>
        </div>
    );
};

export default Overview;
