import React, { useState, useEffect } from 'react';
import { Form, Button, Container, Row, Col, Modal } from 'react-bootstrap'; // Add Modal for Forgot Password
import axios from 'axios';
import './Login.css';

const Login = ({ onLogin }) => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [showPassword, setShowPassword] = useState(false);
    const [error, setError] = useState('');
    const [showForgotPasswordModal, setShowForgotPasswordModal] = useState(false);
    const [resetEmail, setResetEmail] = useState('');
    const [resetMessage, setResetMessage] = useState('');

    useEffect(() => {
        const bootstrap = document.createElement('link');
        bootstrap.rel = 'stylesheet';
        bootstrap.href = 'https://stackpath.bootstrapcdn.com/bootstrap/4.5.2/css/bootstrap.min.css';
        document.head.appendChild(bootstrap);
        return () => {
            document.head.removeChild(bootstrap);
        };
    }, []);

    const handleLogin = async (e) => {
        e.preventDefault();
        if (password.length < 8) {
            setError('Password must be at least 8 characters long.');
            return;
        }
        try {
            const response = await axios.post('http://localhost:5002/login', { email, password });
            const { token, role } = response.data;
            localStorage.setItem('token', token);
            localStorage.setItem('role', role);
            onLogin(role);
        } catch (error) {
            setError('Invalid credentials');
        }
    };

    const togglePasswordVisibility = () => {
        setShowPassword(!showPassword);
    };

    const handleForgotPassword = async () => {
        try {
            const response = await axios.post('http://localhost:5002/forgot-password', { email: resetEmail });
            setResetMessage(response.data.message);
        } catch (error) {
            setResetMessage('Error sending password reset email.');
        }
    };

    return (
        <div className="custom-login-page">
            <Container>
                <Row className="justify-content-center">
                    <Col md={4}>
                        <div className="login-box">
                            <h2>Login</h2>
                            {error && <p style={{ color: 'red' }}>{error}</p>}
                            <Form onSubmit={handleLogin}>
                                <Form.Group controlId="formBasicEmail">
                                    <Form.Label>Email</Form.Label>
                                    <Form.Control
                                        type="email"
                                        placeholder="Enter email"
                                        value={email}
                                        onChange={(e) => setEmail(e.target.value)}
                                    />
                                </Form.Group>

                                <Form.Group controlId="formBasicPassword">
                                    <Form.Label>Password</Form.Label>
                                    <div style={{ position: 'relative' }}>
                                        <Form.Control
                                            type={showPassword ? 'text' : 'password'}
                                            placeholder="Password"
                                            value={password}
                                            onChange={(e) => setPassword(e.target.value)}
                                        />
                                        <span
                                            onClick={togglePasswordVisibility}
                                            style={{
                                                position: 'absolute',
                                                right: '10px',
                                                top: '50%',
                                                transform: 'translateY(-50%)',
                                                cursor: 'pointer',
                                            }}
                                        >
                                            {showPassword ? 'üôà' : 'üëÅÔ∏è'}
                                        </span>
                                    </div>
                                </Form.Group>

                                <Button variant="primary" type="submit" className="w-100 mt-3">
                                    Login
                                </Button>

                                <p className="mt-3">
                                    <a href="#" onClick={() => setShowForgotPasswordModal(true)}>Forgot Password?</a>
                                </p>
                            </Form>
                        </div>
                    </Col>
                </Row>
            </Container>

            {/* Forgot Password Modal */}
            <Modal show={showForgotPasswordModal} onHide={() => setShowForgotPasswordModal(false)}>
                <Modal.Header closeButton>
                    <Modal.Title>Forgot Password</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Form.Group controlId="formResetEmail">
                        <Form.Label>Enter your email address</Form.Label>
                        <Form.Control
                            type="email"
                            placeholder="Enter email"
                            value={resetEmail}
                            onChange={(e) => setResetEmail(e.target.value)}
                        />
                    </Form.Group>
                    {resetMessage && <p>{resetMessage}</p>}
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={() => setShowForgotPasswordModal(false)}>Close</Button>
                    <Button variant="primary" onClick={handleForgotPassword}>Send Reset Link</Button>
                </Modal.Footer>
            </Modal>
        </div>
    );
};

export default Login;
