// LineChart.js
import React from 'react';
import Chart from 'react-apexcharts';

const convertToHoursAndMinutes = (minutes) => {
    const hours = Math.floor(minutes / 60);
    const mins = minutes % 60;
    return `${hours} hours ${mins} minutes`;
};

const convertToHoursAndMinutes1 = (minutes) => {
    const hours = Math.floor(minutes / 60);
    return `${hours} hours`;
};

const LineChart = ({ data, totalData, title, xaxisTitle, yaxisTitle, showTotalPoint, isDefault }) => {
    const chartOptions = {
        chart: {
            type: 'line'
        },
        xaxis: {
            categories: isDefault ? totalData.map(item => item.period) : data.map(item => item.date),
            title: {
                text: xaxisTitle
            }
        },
        yaxis: {
            title: {
                text: yaxisTitle
            },
            labels: {
                formatter: (value) => convertToHoursAndMinutes1(value)
            }
        },
        title: {
            text: title
        },
        stroke: {
            curve: 'smooth'
        },
        dataLabels: {
            enabled: false
        },
        markers: {
            size: 5
        },
        series: [
            {
                name: title,
                data: isDefault ? totalData.map(item => item.value) : data.map(item => item.value)
            }
        ],
        tooltip: {
            y: {
                formatter: (value) => convertToHoursAndMinutes(value)
            }
        },
        colors: ['#00E396', '#FF4560', '#775DD0']
    };

    if (showTotalPoint && !isDefault) {
        const totalValue = data.reduce((acc, item) => acc + item.value, 0);
        chartOptions.series.push({
            name: 'Total',
            data: [...data.map(item => item.value), totalValue],
            markers: {
                size: 8,
                shape: 'square',
                fillColor: '#FF4560',
                strokeColor: '#FF4560',
                strokeWidth: 2,
                hover: {
                    size: 10
                }
            },
            dataLabels: {
                enabled: true,
                formatter: () => 'Total',
                style: {
                    colors: ['#FF4560']
                }
            }
        });
    }

    return (
        <div>
            <Chart options={chartOptions} series={chartOptions.series} type="line" height="350" />
        </div>
    );
};

export default LineChart;
