import React, { useState } from 'react';
import axios from 'axios';
import {
  MDBBtn,
  MDBContainer,
  MDBRow,
  MDBCol,
  MDBInput
} from 'mdb-react-ui-kit';
import './Login.css';  // Custom CSS for any additional styling

const Login = ({ onLogin }) => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');

    const handleLogin = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://localhost:5002/login', { email, password });
            const { token, role } = response.data;
            localStorage.setItem('token', token);
            localStorage.setItem('role', role);
            onLogin(role);
        } catch (error) {
            setError('Invalid credentials');
        }
    };

    return (
        <MDBContainer fluid className="vh-100 d-flex align-items-center justify-content-center gradient-form">
            <MDBRow className="g-0 w-100" style={{ height: '100%' }}>
                <MDBCol md="6" className="d-flex align-items-center justify-content-center" style={{ height: '50%' }}>
                    <div className="w-75">
                        <div className="text-center">
                            <img src="https://mdbcdn.b-cdn.net/img/Photos/new-templates/bootstrap-login-form/lotus.webp"
                                style={{ width: '185px' }} alt="logo" />
                            <h4 className="mt-1 mb-5 pb-1">Welcome Back</h4>
                        </div>
                        <p>Please login to your account</p>
                        {error && <p style={{ color: 'red' }}>{error}</p>}
                        <form onSubmit={handleLogin}>
                            <MDBInput
                                wrapperClass='mb-4'
                                label='Email address'
                                id='form1'
                                type='email'
                                value={email}
                                onChange={(e) => setEmail(e.target.value)}
                            />
                            <MDBInput
                                wrapperClass='mb-4'
                                label='Password'
                                id='form2'
                                type='password'
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                            />
                            <div className="text-center pt-1 mb-5 pb-1">
                                <MDBBtn
                                    className="mb-4 w-100 gradient-custom-2"
                                    type="submit"
                                >
                                    Sign in
                                </MDBBtn>
                            </div>
                        </form>
                    </div>
                </MDBCol>

                <MDBCol md="6" className="gradient-custom-2 d-flex align-items-center justify-content-center" style={{ height: '50%' }}>
                    <div className="text-white px-3 py-4 p-md-5 mx-md-4">
                        <h4 className="mb-4">We are more than just a company</h4>
                        <p className="small mb-0">Lorem ipsum dolor sit amet, consectetur adipisicing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat.</p>
                    </div>
                </MDBCol>
            </MDBRow>
        </MDBContainer>
    );
};

export default Login;
