import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Overview.css';

const Overview = () => {
  const [employeesData, setEmployeesData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const cleanFieldNames = (data) => {
    return data.map((item) => {
      const cleanedItem = {};
      for (const [key, value] of Object.entries(item)) {
        const cleanedKey = key.replace(/^\uFEFF/, ''); // Remove BOM if present
        cleanedItem[cleanedKey] = value;
      }
      return cleanedItem;
    });
  };

  useEffect(() => {
    const fetchEmployeeData = async () => {
      try {
        const [employeeResponse, timesheetResponse, companyDetailsResponse] = await Promise.all([
          axios.get('http://localhost:5001/Employee_Data1'),
          axios.get('http://localhost:5001/timesheet'),
          axios.get('http://localhost:5001/CompanyDetails'),
        ]);

        const employeeData = employeeResponse.data;
        const timesheetData = timesheetResponse.data;
        const rawCompanyDetailsData = companyDetailsResponse.data;

        // Clean the company details data
        const companyDetailsData = cleanFieldNames(rawCompanyDetailsData);

        // Process unique user_id and project pairs
        const userProjectCompanyMap = new Map();
        timesheetData.forEach((entry) => {
          const { User_Id, project } = entry;
          const company = companyDetailsData.find(
            (company) => company.company_id === project
          );

          if (company) {
            userProjectCompanyMap.set(`${User_Id}-${project}`, company.company_name);
          }
        });

        // console.log("Mapped User_Id, Project, and Company_Name:");
        // userProjectCompanyMap.forEach((companyName, key) => {
        //   const [userId, project] = key.split('-');
        //   const employee = employeeData.find(emp => emp.Emp_ID === userId);
        //   if (employee) {
        //     const fullName = `${employee.User_Fname} ${employee.User_Lname}`;
        //     console.log(`User_Id: ${userId}, Project: ${project}, Company_Name: ${companyName}, Full Name: ${fullName}`);
        //   } else {
        //     console.log(`User_Id: ${userId}, Project: ${project}, Company_Name: ${companyName}`);
        //   }
        // });

        // Process employee data for display in table
        const processedData = employeeData.map((employee) => {
        //   const projectInfo = timesheetData.find((timesheet) => timesheet.User_Id === employee.Emp_ID);
          const companyName = projectInfo
            ? userProjectCompanyMap.get(`${employee.Emp_ID}-${projectInfo.project}`)
            : 'No project assigned';

          return {
            name: `${employee.User_Fname} ${employee.User_Lname}` || 'N/A',
            email: employee.User_Email || 'N/A',
            status: employee.User_Status || 'N/A',
            project: projectInfo ? projectInfo.project : 'No project assigned',
            companyName: companyName || 'N/A',
          };
        });

        setEmployeesData(processedData);
      } catch (error) {
        setError('An error occurred while fetching employee data.');
      } finally {
        setLoading(false);
      }
    };

    fetchEmployeeData();
  }, []);

  return (
    <div className="Overview-container">
      <h1 className="title">Employee Details</h1>
      <div className="EmployeeTable">
        <h2>Employees Details</h2>
        {loading && !error ? (
          <p>Loading...</p>
        ) : error ? (
          <p>{error}</p>
        ) : (
          <table>
            <thead>
              <tr>
                <th>Employee Name</th>
                <th>Email</th>
                <th>Status</th>
                <th>Project</th>
                <th>Company Name</th>
              </tr>
            </thead>
            <tbody>
              {employeesData.map((employee, index) => (
                <tr key={index}>
                  <td>{employee.name}</td>
                  <td>{employee.email}</td>
                  <td>{employee.status}</td>
                  <td>{employee.project}</td>
                  <td>{employee.companyName}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
};

export default Overview;
