import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Overview.css';

const Overview = () => {
  const [employeesData, setEmployeesData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchEmployeeData = async () => {
      try {
        // Fetch data from the APIs
        const [employeeResponse, timesheetResponse, companyDetailsResponse] = await Promise.all([
          axios.get('http://localhost:5001/Employee_Data1'),
          axios.get('http://localhost:5001/timesheet'),
          axios.get('http://localhost:5001/CompanyDetails'),
        ]);

        const employeeData = employeeResponse.data;
        const timesheetData = timesheetResponse.data;
        const companyDetailsData = companyDetailsResponse.data;

        // Create a map for company IDs to company names
        const companyMap = companyDetailsData.reduce((acc, company) => {
          acc[company.company_id] = company.company_name;
          return acc;
        }, {});

        console.log("Company Map: ", companyMap);

        // Create a map of employees with their respective project names
        const employeeProjectsMap = timesheetData.reduce((acc, timesheetEntry) => {
          const userId = timesheetEntry.User_Id; // Ensure this is the correct field name
          const projectId = timesheetEntry.project; // Ensure this is the correct field name
          const projectName = companyMap[projectId] || 'Unknown Project';

          if (!acc[userId]) {
            acc[userId] = new Set(); // Use a Set to avoid duplicate project names
          }
          acc[userId].add(projectName);

          return acc;
        }, {});

        // Convert Sets back to comma-separated strings for display
        const employeeProjectsListMap = Object.fromEntries(
          Object.entries(employeeProjectsMap).map(([userId, projectSet]) => [userId, [...projectSet].join(', ')])
        );

        console.log("Employee Projects Map: ", employeeProjectsMap);

        // Combine employee data with their project names
        const combinedData = employeeData.map((employee) => {
          const employeeProjectsList = employeeProjectsListMap[employee.Emp_ID] || 'No project assigned';

          return {
            name: employee.User_Fname || 'N/A',
            email: employee.User_Email || 'N/A',
            status: employee.User_Status || 'N/A',
            projects: employeeProjectsList,
          };
        });

        console.log("Combined Data: ", combinedData);

        setEmployeesData(combinedData);
      } catch (error) {
        setError('An error occurred while fetching employee data.');
      } finally {
        setLoading(false);
      }
    };

    fetchEmployeeData();
  }, []);


  return (
    <div className="Overview-container">
      <h1 className="title">Employee Project Details</h1>
      <div className="EmployeeTable">
        <h2>Employees Details</h2>
        {loading && !error ? (
          <p>Loading...</p>
        ) : error ? (
          <p>{error}</p>
        ) : (
          <table>
            <thead>
              <tr>
                <th>Employee Name</th>
                <th>Email</th>
                <th>Status</th>
                <th>Projects</th>
              </tr>
            </thead>
            <tbody>
              {employeesData.map((employee, index) => (
                <tr key={index}>
                  <td>{employee.name}</td>
                  <td>{employee.email}</td>
                  <td>{employee.status}</td>
                  <td>{employee.projects}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
};

export default Overview;
