import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { PieChart, Pie, Cell, Legend, Tooltip } from 'recharts';

const Overview = () => {
    const [data, setData] = useState([]);
    const [totalEmployees, setTotalEmployees] = useState(0);
    const [statusData, setStatusData] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await axios.get('http://localhost:5001/data');
                const fetchedData = response.data;
                setData(fetchedData);

                // Total number of employees
                const uniqueEmployees = new Set(fetchedData.map(item => item.emp_id));
                setTotalEmployees(uniqueEmployees.size);

                // Status counts
                const statusCount = fetchedData.reduce((acc, { status }) => {
                    acc[status] = (acc[status] || 0) + 1;
                    return acc;
                }, {});

                setStatusData(Object.keys(statusCount).map(status => ({
                    name: status,
                    value: statusCount[status]
                })));
            } catch (error) {
                console.error("Error fetching data:", error);
            }
        };

        fetchData();
    }, []);

    return (
        <div>
            <h1>Overview</h1>
            <div>Total Employees: {totalEmployees}</div>
            <div>
                <h2>Status Distribution</h2>
                <PieChart width={400} height={400}>
                    <Pie
                        data={statusData}
                        dataKey="value"
                        nameKey="name"
                        outerRadius={150}
                        fill="#8884d8"
                        label
                    >
                        {statusData.map((entry, index) => (
                            <Cell key={`cell-${index}`} fill={entry.name === 'Approved' ? '#00C49F' : entry.name === 'Unapproved' ? '#FF8042' : '#FFBB28'} />
                        ))}
                    </Pie>
                    <Tooltip />
                    <Legend />
                </PieChart>
            </div>
        </div>
    );
};

export default Overview;
