import { text } from 'express';
import React from 'react';
import Chart from 'react-apexcharts';

const convertToHoursAndMinutes = (minutes) => {
    const hours = Math.floor(minutes / 60);
    const mins = minutes % 60;
    return `${hours} hours ${mins} minutes`;
};

const convertToHoursAndMinutes1 = (minutes) => {
    const hours = Math.floor(minutes / 60);
    return `${hours} hours`;
};

const LineChart = ({ data, totalData, title, xaxisTitle, yaxisTitle, showTotalPoint, isDefault }) => {

    const uniqueDates = [...new Set(data.map(item => item.date))];

    const chartOptions = {
        chart: {
            type: 'line'
        },
        xaxis: [
            {categories: uniqueDates,
            title: {
                text: xaxisTitle
            }
        },
        {
            opposite: true,
            title: {
                text: xaxisTitle
            },
            labels: {
                formatter: (value) => convertToHoursAndMinutes1(value)
            }
        }

        ],
        yaxis: {
            title: {
                text: yaxisTitle
            },
            labels: {
                formatter: (value) => convertToHoursAndMinutes1(value)
            }
        },
        title: {
            text: title
        },
        stroke: {
            curve: 'smooth'
        },
        dataLabels: {
            enabled: false
        },
        markers: {
            size: 5,
            colors: ['#FF4560', '#00E396']
        },
        series: [
            {
                name: 'Daily Time',
                data: uniqueDates.map(date => {
                    const entry = data.find(item => item.date === date);
                    return entry ? entry.value : 0;
                }),
                color: '#FF4560'
            },
            {
                name: 'Total Time',
                data: totalData.map(item => item.value),
                color: '#00E396',
                xAxisIndex: 1
            }
        ],
        tooltip: {
            y: {
                formatter: (value) => convertToHoursAndMinutes(value)
            }
        }
    };

    // Handle case for default filter type
    if (isDefault) {
        chartOptions.xaxis.categories = data.map(item => item.date.substring(0, 7)); // Extract month part
        chartOptions.series = [
            {
                name: 'Total Time',
                data: totalData.map(item => item.value),
                color: '#00E396'
            }
        ];
    }

    return (
        <div>
            <Chart options={chartOptions} series={chartOptions.series} type="line" height="350" />
        </div>
    );
};

export default LineChart;
