import React, { useEffect, useState } from 'react';
import { Routes, Route, useNavigate } from 'react-router-dom';
import Overview from './components/Overview';
import Sidebar from './components/Sidebar';
import Project from './components/Project';
import Login from './components/Login';
import './App.css';

function App() {
  const [role, setRole] = useState(null);
  const navigate = useNavigate();  // useNavigate can be used here now

  useEffect(() => {
    // Check for token and role in localStorage
    const savedToken = localStorage.getItem('token');
    const savedRole = localStorage.getItem('role');
    if (savedToken && savedRole) {
      setRole(parseInt(savedRole));
    }
  }, []);

  useEffect(() => {
    if (role === 4) {
      // Redirect Project Managers directly to the project section
      navigate('/Project');
    } else if (role === 5) {
      // Redirect Employees directly to the overview section
      navigate('/');
    }
    // Admin (role 2) will not have forced redirection and can freely navigate
  }, [role, navigate]);

  const handleLogin = (role) => {
    setRole(role);
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('role');
    setRole(null);
    navigate('/');  // Redirect to login page on logout
  };

  if (!role) {
    return <Login onLogin={handleLogin} />;
  }

  // return (
