// LineChart.js
import React from 'react';
import Chart from 'react-apexcharts';

const LineChart = ({ data, title, xaxisTitle, yaxisTitle }) => {
    const chartOptions = {
        chart: {
            type: 'line'
        },
        xaxis: {
            categories: data.map(item => item.month),
            title: {
                text: xaxisTitle
            }
        },
        yaxis: {
            title: {
                text: yaxisTitle
            }
        },
        title: {
            text: title
        },
        stroke: {
            curve: 'smooth'
        },
        dataLabels: {
            enabled: true
        },
        markers: {
            size: 5
        },
        series: [
            {
                name: title,
                data: data.map(item => item.value)
            }
        ],
        colors: ['#00E396', '#FF4560', '#775DD0']
    };

    return (
        <div>
            <Chart options={chartOptions} series={chartOptions.series} type="line" height="350" />
        </div>
    );
};

export default LineChart;
