import React, { useState } from 'react';
import axios from 'axios';
import {
  MDBBtn,
  MDBContainer,
  MDBRow,
  MDBCol,
  MDBInput,
  MDBIcon
} from 'mdb-react-ui-kit';
import './Login.css';  // Custom CSS for any additional styling

const Login = ({ onLogin }) => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');

    const handleLogin = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://localhost:5002/login', { email, password });
            const { token, role } = response.data;
            localStorage.setItem('token', token);
            localStorage.setItem('role', role);
            onLogin(role);
        } catch (error) {
            setError('Invalid credentials');
        }
    };

    return (
        <MDBContainer fluid className="vh-100">
            <MDBRow className="h-100">

                {/* Left half with login form */}
                <MDBCol sm='6' className='d-flex flex-column justify-content-center align-items-center'>

                    <div className='d-flex flex-row ps-5'>
                        <MDBIcon fas icon="crow fa-3x me-3" style={{ color: '#709085' }}/>
                        <span className="h1 fw-bold mb-0">Logo</span>
                    </div>

                    <div className='d-flex flex-column justify-content-center w-75 pt-4'>

                        <h3 className="fw-normal mb-3 ps-5 pb-3" style={{ letterSpacing: '1px' }}>Log in</h3>

                        <MDBInput
                            wrapperClass='mb-4 mx-5 w-100'
                            label='Email address'
                            id='form1'
                            type='email'
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            size="lg"
                        />
                        <MDBInput
                            wrapperClass='mb-4 mx-5 w-100'
                            label='Password'
                            id='form2'
                            type='password'
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            size="lg"
                        />

                        <MDBBtn className="mb-4 px-5 mx-5 w-100" color='info' size='lg' type="submit">
                            Sign in
                        </MDBBtn>

                        {error && <p style={{ color: 'red' }}>{error}</p>}
                        <p className="small mb-5 pb-lg-3 ms-5"><a className="text-muted" href="#!">Forgot password?</a></p>
                        <p className='ms-5'>Don't have an account? <a href="#!" className="link-info">Register here</a></p>

                    </div>
                </MDBCol>

                {/* Right half with image */}
                <MDBCol sm='6' className='d-none d-sm-block px-0'>
                    <img
                        src="https://mdbcdn.b-cdn.net/img/Photos/new-templates/bootstrap-login-form/img3.webp"
                        alt="Login image"
                        className="w-100 h-100"
                        style={{ objectFit: 'cover', objectPosition: 'left' }}
                    />
                </MDBCol>

            </MDBRow>
        </MDBContainer>
    );
};

export default Login;
