import React, { useState, useEffect } from 'react';
import axios from 'axios';
import GradientPieChart from './GradientPieChart'; 
import './Overview.css'; 

const Overview = () => {
  const [totalEmployees, setTotalEmployees] = useState(0);
  const [departmentCounts, setDepartmentCounts] = useState({});
  const [statusCounts, setStatusCounts] = useState({});
  const [totalTime, setTotalTime] = useState(0); // State for total time
  const [filterType, setFilterType] = useState('day'); // 'day', 'week', 'month'
  const [filterValue, setFilterValue] = useState('2024-05-01'); // Initial filter value

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('http://localhost:5001/data');
        const csvData = response.data;

        // Count unique employees
        const uniqueNames = new Set(csvData.map(item => item.emp_name));
        setTotalEmployees(uniqueNames.size);

        // Count unique departments
        const deptCounts = csvData.reduce((acc, item) => {
          if (!acc[item.department]) {
            acc[item.department] = new Set();
          }
          acc[item.department].add(item.emp_name);
          return acc;
        }, {});
        const departmentCountMap = Object.keys(deptCounts).reduce((acc, department) => {
          acc[department] = deptCounts[department].size;
          return acc;
        }, {});
        setDepartmentCounts(departmentCountMap);

        // Count status
        const statusCounts = csvData.reduce((acc, item) => {
          acc[item.status] = (acc[item.status] || 0) + 1;
          return acc;
        }, {});
        setStatusCounts(statusCounts);

        // Calculate total hours based on filter
        let filteredData = csvData;
        if (filterType === 'day') {
          filteredData = csvData.filter(item => item.date === filterValue);
        } else if (filterType === 'week') {
          const [startDate, endDate] = filterValue.split(' to ');
          filteredData = csvData.filter(item => item.date >= startDate && item.date <= endDate);
        } else if (filterType === 'month') {
          filteredData = csvData.filter(item => item.date.startsWith(filterValue));
        }

        const totalMinutes = filteredData.reduce((acc, item) => acc + convertToMinutes(item.total_hrs), 0);
        setTotalTime(totalMinutes);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
    const interval = setInterval(fetchData, 2000); 

    return () => clearInterval(interval);
  }, [filterType, filterValue]);

  const convertToHoursAndMinutes = (minutes) => {
    const hours = Math.floor(minutes / 60);
    const mins = minutes % 60;
    return `${hours} hours ${mins} minutes`;
  };

  return (
    <div className='Overview-container'>
        <h1>Employee Time Sheet</h1>
        <div className='filter-container'>
            <select value={filterType} onChange={(e) => setFilterType(e.target.value)}>
              <option value='day'>Day</option>
              <option value='week'>Week</option>
              <option value='month'>Month</option>
            </select>

            {filterType === 'day' && (
              <input 
                type='date' 
                value={filterValue} 
                onChange={(e) => setFilterValue(e.target.value)} 
              />
            )}

            {filterType === 'week' && (
              <input 
                type='text' 
                placeholder='2024-05-01 to 2024-05-07'
                value={filterValue} 
                onChange={(e) => setFilterValue(e.target.value)} 
              />
            )}

            {filterType === 'month' && (
              <select value={filterValue} onChange={(e) => setFilterValue(e.target.value)}>
                <option value='2024-05'>May</option>
                <option value='2024-06'>June</option>
                <option value='2024-07'>July</option>
                {/* Add more months as needed */}
              </select>
            )}
        </div>

        <div className='counts-container'>
            <p className='EmployeeCount'>Total Number of Employees: {totalEmployees}</p>
            <p className='TimeCount'>Total Time: {convertToHoursAndMinutes(totalTime)}</p>
        </div>

        <div className='final-container'>
            <div className='chart-container'>
                <div className='chart1'>
                    <h2>Status Breakdown</h2>  
                    <GradientPieChart
                        data={Object.entries(statusCounts).map(([key, value]) => ({ name: key, value }))}
                    />
                </div>
            </div>

            <div className='chart-container'> 
                <div className='chart2'>
                    <h1>Department Breakdown</h1>
                    <GradientPieChart
                        data={Object.entries(departmentCounts).map(([key, value]) => ({ name: key, value }))}
                    />
                </div>
            </div>
        </div>
    </div>
  );
};

export default Overview;
