import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Overview.css';

const Overview = () => {
  const [employeesData, setEmployeesData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchEmployeeData = async () => {
      try {
        // Fetch data from the APIs
        const [employeeResponse, timesheetResponse, companyDetailsResponse] = await Promise.all([
          axios.get('http://localhost:5001/Employee_Data1'),
          axios.get('http://localhost:5001/timesheet'),
          axios.get('http://localhost:5001/CompanyDetails'),
        ]);

        const employeeData = employeeResponse.data;
        const timesheetData = timesheetResponse.data;
        const companyDetailsData = companyDetailsResponse.data;

        // Process unique user_id and project pairs
        const projectToCompanyMap = companyDetailsData.reduce((acc, company) => {
            const companyId = company.company_id;
            const companyName = company.company_name;

            if (companyId && companyName) {
              // Assume project ID in timesheet matches company_id here
              acc[companyId] = companyName;
            } else {
              console.warn(`Missing company_id or company_name in company data:`, company);
            }

            return acc;
          }, {});

          // Create a map of unique User_Id and Project pairs with company names
          const uniqueUserProjects = new Set();

          timesheetData.forEach((entry) => {
            const userId = entry.User_Id;
            const projectId = entry.project;
            const companyName = projectToCompanyMap[projectId] || 'Unknown Company';

            if (userId && projectId) {
              uniqueUserProjects.add(`${userId}, ${projectId}, ${companyName}`);
            }
          });

          // Log the unique User_Id and Project pairs with company names
          console.log("Unique User_Id and Project with Company_name:");
          uniqueUserProjects.forEach((entry) => console.log(entry));


        // Process employee data for display (if needed later)
        const processedData = employeeData.map((employee) => ({
          name: employee.User_Fname + " " + employee.User_Lname || 'N/A',
          email: employee.User_Email || 'N/A',
          status: employee.User_Status || 'N/A',
        }));

        setEmployeesData(processedData);
      } catch (error) {
        setError('An error occurred while fetching employee data.');
      } finally {
        setLoading(false);
      }
    };

    fetchEmployeeData();
  }, []);

  return (
    <div className="Overview-container">
      <h1 className="title">Employee Details</h1>
      <div className="EmployeeTable">
        <h2>Employees Details</h2>
        {loading && !error ? (
          <p>Loading...</p>
        ) : error ? (
          <p>{error}</p>
        ) : (
          <table>
            <thead>
              <tr>
                <th>Employee Name</th>
                <th>Email</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {employeesData.map((employee, index) => (
                <tr key={index}>
                  <td>{employee.name}</td>
                  <td>{employee.email}</td>
                  <td>{employee.status}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
};

export default Overview;
