// Overview.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import GradientPieChart from './GradientPieChart';
import LineChart from './LineChart';
import './Overview.css';

const Overview = () => {
    const [totalEmployees, setTotalEmployees] = useState(0);
    const [departmentCounts, setDepartmentCounts] = useState({});
    const [statusCounts, setStatusCounts] = useState({});
    const [totalTime, setTotalTime] = useState('0 hours 0 minutes');
    const [filterType, setFilterType] = useState('default');
    const [filterValue, setFilterValue] = useState('');
    const [startDate, setStartDate] = useState('');
    const [endDate, setEndDate] = useState('');
    const [timeData, setTimeData] = useState([]);
    const [breakData, setBreakData] = useState([]);

    const convertToMinutes = (time) => {
        const [hours, decimalMinutes] = time.split('.').map(Number);
        const minutes = Math.round(decimalMinutes);
        return (hours * 60) + minutes;
    };

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await axios.get('http://localhost:5001/data');
                const csvData = response.data;

                const uniqueNames = new Set(csvData.map(item => item.emp_name));
                setTotalEmployees(uniqueNames.size);

                const deptCounts = csvData.reduce((acc, item) => {
                    if (!acc[item.department]) {
                        acc[item.department] = new Set();
                    }
                    acc[item.department].add(item.emp_name);
                    return acc;
                }, {});
                const departmentCountMap = Object.keys(deptCounts).reduce((acc, department) => {
                    acc[department] = deptCounts[department].size;
                    return acc;
                }, {});
                setDepartmentCounts(departmentCountMap);

                let filteredData = csvData;
                if (filterType === 'day') {
                    filteredData = csvData.filter(item => item.date === filterValue);
                } else if (filterType === 'week') {
                    filteredData = csvData.filter(item => item.date >= startDate && item.date <= endDate);
                } else if (filterType === 'month') {
                    filteredData = csvData.filter(item => item.date.startsWith(filterValue));
                }

                const statusCounts = filteredData.reduce((acc, item) => {
                    acc[item.status] = (acc[item.status] || 0) + 1;
                    return acc;
                }, {});
                setStatusCounts(statusCounts);

                let totalMinutes = filteredData.reduce((acc, item) => acc + convertToMinutes(item.total_hrs), 0);

                let overflowMinutes = totalMinutes % 60;
                let overflowHours = Math.floor(totalMinutes / 60);

                setTotalTime(`${overflowHours} hours ${overflowMinutes} minutes`);

                const timeData = filteredData.reduce((acc, item) => {
                    const date = item.date;
                    if (!acc[date]) {
                        acc[date] = 0;
                    }
                    acc[date] += convertToMinutes(item.total_hrs);
                    return acc;
                }, {});

                const breakData = filteredData.reduce((acc, item) => {
                    const date = item.date;
                    if (!acc[date]) {
                        acc[date] = 0;
                    }
                    acc[date] += convertToMinutes(item.break);
                    return acc;
                }, {});

                setTimeData(Object.entries(timeData).map(([date, value]) => ({ date, value })));
                setBreakData(Object.entries(breakData).map(([date, value]) => ({ date, value })));

            } catch (error) {
                console.error('Error fetching data:', error);
            }
        };

        fetchData();
        const interval = setInterval(fetchData, 2000);

        return () => clearInterval(interval);
    }, [filterType, filterValue, startDate, endDate]);

    const handleFilterChange = (e) => {
        setFilterType(e.target.value);
        setFilterValue(''); // Reset filter value when changing type
        setStartDate('');
        setEndDate('');
    };

    const handleFilterValueChange = (e) => {
        setFilterValue(e.target.value);
    };

    const handleStartDateChange = (e) => {
        setStartDate(e.target.value);
    };

    const handleEndDateChange = (e) => {
        setEndDate(e.target.value);
    };

    return (
        <div className='Overview-container'>
            <h1>Employee Time Sheet</h1>
            <div className='filter-container'>
                <select value={filterType} onChange={handleFilterChange}>
                    <option value='default'>Default</option>
                    <option value='day'>Day</option>
                    <option value='week'>Week</option>
                    <option value='month'>Month</option>
                </select>

                {filterType === 'day' && (
                    <input
                        type='date'
                        value={filterValue}
                        onChange={handleFilterValueChange}
                    />
                )}

                {filterType === 'week' && (
                    <>
                        <input
                            type='date'
                            value={startDate}
                            onChange={handleStartDateChange}
                        />
                        <input
                            type='date'
                            value={endDate}
                            onChange={handleEndDateChange}
                        />
                    </>
                )}

                {filterType === 'month' && (
                    <input
                        type='month'
                        value={filterValue}
                        onChange={handleFilterValueChange}
                    />
                )}
            </div>

            <div className='counts-container'>
                <p className='EmployeeCount'>Total Number of Employees: {totalEmployees}</p>
                <p className='TimeCount'>Total Time: {totalTime}</p>
            </div>

            <div className='final-container'>
                <div className='chart-container'>
                    <div className='chart1'>
                        <h2>Status Breakdown</h2>
                        <GradientPieChart
                            data={Object.entries(statusCounts).map(([key, value]) => ({ name: key, value }))}
                        />
                    </div>
                </div>

                <div className='chart-container'>
                    <div className='chart2'>
                        <h2>Department Breakdown</h2>
                        <GradientPieChart
                            data={Object.entries(departmentCounts).map(([key, value]) => ({ name: key, value }))}
                        />
                    </div>
                </div>
            </div>

            <div className='final-container1'>
                <div className='chart-container1'>
                    <div className='line-chart1'>
                        <h2>Daily Total Time</h2>
                        <LineChart
                            data={timeData}
                            title="Total Time"
                            xaxisTitle="Date"
                            yaxisTitle="Total Time (Minutes)"
                        />
                    </div>
                </div>

                <div className='chart-container1'>
                    <div className='line-chart2'>
                        <h2>Daily Break Time</h2>
                        <LineChart
                            data={breakData}
                            title="Break Time"
                            xaxisTitle="Date"
                            yaxisTitle="Break Time (Minutes)"
                        />
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Overview;
