import React from 'react';
import ReactApexChart from 'react-apexcharts';

const BarChart = ({ data, title }) => {
  const categories = data.map(item => item.name);
  const seriesData = data.map(item => item.value);

  const dynamicHeight = Math.max(400, data.length * 20); // Adjust height dynamically based on the number of items

  const options = {
    chart: {
      type: 'bar',
      height: dynamicHeight, // Set dynamic height
    },
    plotOptions: {
      bar: {
        horizontal: false, // Use vertical bar chart
        columnWidth: '50%', // Adjust column width to fit more bars
      },
    },
    dataLabels: {
      enabled: false,
    },
    xaxis: {
      categories,
      labels: {
        rotate: -45, // Rotate labels for better readability
        maxHeight: 100, // Set a maximum height for labels
        style: {
          fontSize: '12px', // Adjust the font size
        },
        formatter: function(value) {
          return value.length > 10 ? value.substring(0, 10) + '...' : value; // Truncate long labels
        },
      },
    },
    yaxis: {
      labels: {
        style: {
          fontSize: '12px',
        },
      },
      title: {
        text: 'Number of Employees',
      },
    },
    title: {
      text: title,
      align: 'center',
      style: {
        fontSize: '18px',
      },
    },
    fill: {
      colors: ['#008FFB', '#00E396', '#FEB019', '#FF4560', '#775DD0', '#66DA26', '#546E7A', '#D4526E', '#8D5B4C', '#F86624'],
    },
    tooltip: {
      y: {
        formatter: function(val) {
          return val + " employees";
        },
      },
    },
    grid: {
      xaxis: {
        lines: {
          show: false, // Remove grid lines to reduce visual clutter
        },
      },
    },
  };

  const series = [
    {
      name: title,
      data: seriesData,
    },
  ];

  return (
    <div className="chart">
      <ReactApexChart options={options} series={series} type="bar" height={dynamicHeight} />
    </div>
  );
};

export default BarChart;
