import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { PieChart, Pie, Cell, Legend, Tooltip } from 'recharts';
import './Overview.css'; // Import the CSS file for styling

const Overview = () => {
  const [totalEmployees, setTotalEmployees] = useState(0);
  const [departmentData, setDepartmentData] = useState([]);
  const [statusCounts, setStatusCounts] = useState({});

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('http://localhost:5001/data');
        const csvData = response.data;

        // Count unique employees
        const uniqueNames = new Set(csvData.map(item => item.emp_name));
        setTotalEmployees(uniqueNames.size);

        // Aggregate departments (assuming only one department)
        const departmentCount = csvData.length;
        setDepartmentData([{ name: 'Product', value: departmentCount }]);

        // Count status
        const statusCounts = csvData.reduce((acc, item) => {
          acc[item.status] = (acc[item.status] || 0) + 1;
          return acc;
        }, {});
        setStatusCounts(statusCounts);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
    const interval = setInterval(fetchData, 5000); // Poll every 5 seconds

    return () => clearInterval(interval);
  }, []);

  // Pie chart colors
  const COLORS = ['#0088FE', '#00C49F', '#FFBB28', '#FF8042'];

  return (
    <div>
      <h1>Overview</h1>
      <p>Total Number of Employees: {totalEmployees}</p>

      <div className='chart-container'>
        <div className='chart1'>
          <h2>Status Breakdown</h2>
          <PieChart width={600} height={400}>
            <Pie
              data={Object.entries(statusCounts).map(([key, value]) => ({ name: key, value }))}
              dataKey="value"
              cx="50%"
              cy="50%"
              outerRadius={150}
              fill="#8884d8"
              label
            >
              {Object.keys(statusCounts).map((entry, index) => (
                <Cell key={entry} fill={COLORS[index % COLORS.length]} />
              ))}
            </Pie>
            <Tooltip />
            <Legend />
          </PieChart>
        </div>

        <div className='chart2'>
          <h2>Department Breakdown</h2>
          <PieChart width={600} height={400}>
            <Pie
              data={departmentData}
              dataKey="value"
              cx="50%"
              cy="50%"
              outerRadius={150}
              fill="#8884d8"
              label
            >
              <Cell fill={COLORS[0]} />
            </Pie>
            <Tooltip />
            <Legend />
          </PieChart>
        </div>
      </div>
    </div>
  );
};

export default Overview;
