import React, { useState, useEffect } from 'react';
import { MapContainer, TileLayer, Marker, Popup, useMap } from 'react-leaflet';
import { GeoSearchControl, OpenStreetMapProvider } from 'leaflet-geosearch';
import 'leaflet/dist/leaflet.css';
import 'leaflet-geosearch/dist/geosearch.css';
import './Location.css';

const SearchField = () => {
    const map = useMap();

    useEffect(() => {
        const provider = new OpenStreetMapProvider();

        const searchControl = new GeoSearchControl({
            provider,
            style: 'bar',
            showMarker: true,
            retainZoomLevel: false,
            animateZoom: true,
            autoClose: true,
            searchLabel: 'Enter address'
        });

        map.addControl(searchControl);
        return () => map.removeControl(searchControl);
    }, [map]);

    return null;
};

const Location = () => {
    const [leavesData, setLeavesData] = useState([]);
    const [geoData, setGeoData] = useState([]);

    useEffect(() => {
        const fetchLeavesData = async () => {
            try {
                const response = await fetch('http://localhost:5001/data');
                if (!response.ok) {
                    throw new Error(`HTTP error! status: ${response.status}`);
                }
                const data = await response.json();
                console.log('Fetched data:', data);
                setLeavesData(data);
            } catch (error) {
                console.error('Error fetching leaves data:', error);
            }
        };

        fetchLeavesData();
    }, []);

    useEffect(() => {
        const geocodeLocation = async (location) => {
            const url = `https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(location)}`;
            for (let attempt = 1; attempt <= 3; attempt++) {
                try {
                    const response = await fetch(url);
                    if (!response.ok) {
                        throw new Error(`HTTP error! status: ${response.status}`);
                    }
                    const result = await response.json();
                    if (result.length > 0) {
                        const { lat, lon } = result[0];
                        return { lat, lon };
                    }
                    break;
                } catch (error) {
                    console.error(`Error geocoding ${location} (attempt ${attempt}):`, error);
                    if (attempt === 3) {
                        return null;
                    }
                }
            }
        };

        const geocodeLocations = async () => {
            const geocodedData = await Promise.all(leavesData.map(async (item) => {
                const geocodeResult = await geocodeLocation(item.location);
                if (geocodeResult) {
                    return {
                        ...item,
                        lat: geocodeResult.lat,
                        lon: geocodeResult.lon
                    };
                }
                return null;
            }));

            setGeoData(geocodedData.filter(item => item !== null));
        };

        if (leavesData.length > 0) {
            geocodeLocations();
        }
    }, [leavesData]);

    const defaultCenter = [12.9716, 77.5946]; // Coordinates for Bengaluru

    return (
        <MapContainer center={defaultCenter} zoom={13} style={{ height: "100vh", width: "100px" }}>
            <TileLayer
                url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
            />
            <SearchField />
            {geoData.map((location, idx) => (
                <Marker key={idx} position={[location.lat, location.lon]}>
                    <Popup>
                        {location.location}: {location.emp_name} - {location.leaves} leaves
                    </Popup>
                </Marker>
            ))}
        </MapContainer>
    );
};

export default Location;
