import React from 'react';
import ReactApexChart from 'react-apexcharts';

const GradientPieChart = ({ series, labels, title }) => {
  const options = {
    chart: {
      type: 'donut',
    },
    plotOptions: {
      pie: {
        startAngle: -90,
        endAngle: 270,
        donut: {
          size: '70%',
          background: 'transparent',
        },
      },
    },
    fill: {
      type: 'gradient',
    },
    dataLabels: {
      enabled: true,
      formatter: (val, { seriesIndex }) => series[seriesIndex], // Show count values
    },
    tooltip: {
      y: {
        formatter: (val) => val, // Show count values
      },
    },
    legend: {
      position: 'bottom',
    },
    title: {
      text: title,
      align: 'center',
    },
    labels,
  };

  return (
    <ReactApexChart
      options={options}
      series={series}
      type="donut"
      width={380}
    />
  );
};

export default GradientPieChart;
