// GradientPieChart.js
import React from 'react';
import ReactApexChart from 'react-apexcharts';

const GradientPieChart = ({ data, title, statusColorMapping }) => {
  const series = data.map(item => item.value);
  const labels = data.map(item => item.name);

  const generateColors = () => {
    return labels.map(label => statusColorMapping[label] || '#000'); // Default to black if no mapping
  };

  const options = {
    chart: {
      type: 'donut',
    },
    plotOptions: {
      pie: {
        startAngle: -90,
        endAngle: 270,
        donut: {
          size: '70%',
          background: 'transparent',
        },
      },
    },
    fill: {
      type: 'gradient',
      gradient: {
        shade: 'dark',
        type: 'vertical',
        shadeIntensity: 0.5,
        gradientToColors: generateColors(), // Apply gradient colors
        inverseColors: true,
        opacityFrom: 1,
        opacityTo: 1,
        stops: [0, 100],
      },
    },
    dataLabels: {
      enabled: false,
    },
    legend: {
      position: 'bottom',
      formatter: function (val, opts) {
        return val + " - " + opts.w.globals.series[opts.seriesIndex];
      },
    },
    labels,
  };

  return (
    <div className="chart">
      <ReactApexChart options={options} series={series} type="donut" height={400} />
    </div>
  );
};

export default GradientPieChart;
