import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Overview.css';

const Overview = () => {
  const [employeesData, setEmployeesData] = useState([]);
  const [projects, setProjects] = useState({});
  const [timesheetData, setTimesheetData] = useState([]);

  useEffect(() => {
    const fetchEmployeeData = async () => {
      try {
        // Fetch data from Employee_Data1 API
        const employeeResponse = await axios.get('http://localhost:5001/Employee_Data1');
        const timesheetResponse = await axios.get('http://localhost:5001/timesheet');
        const companyDetailsResponse = await axios.get('http://localhost:5001/CompanyDetails');

        const employeeData = employeeResponse.data;
        const timesheetData = timesheetResponse.data;
        const companyDetailsData = companyDetailsResponse.data;

        // Group projects by company ID for easier lookup
        const projectMapping = companyDetailsData.reduce((acc, project) => {
          acc[project.company_id] = project.company_name;
          return acc;
        }, {});

        // Map employee data with projects
        const employeeProjects = timesheetData.reduce((acc, timesheetEntry) => {
          const userId = timesheetEntry.User_Id;
          const projectId = timesheetEntry.project;
          const projectName = projectMapping[projectId] || 'Unknown Project';

          if (!acc[userId]) {
            acc[userId] = new Set();
          }
          acc[userId].add(projectName);

          return acc;
        }, {});

        // Combine employee data with project data
        const combinedData = employeeData.map((employee) => {
          const employeeProjectsList = employeeProjects[employee.User_Id]
            ? Array.from(employeeProjects[employee.User_Id]).join(', ')
            : 'No project assigned';

          return {
            name: employee.emp_name,
            email: employee.email,
            status: employee.User_status,
            projects: employeeProjectsList,
          };
        });

        setEmployeesData(combinedData);
        setTimesheetData(timesheetData);
        setProjects(projectMapping);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchEmployeeData();
  }, []);

  return (
    <div className='Overview-container'>
      <h1 className='title'>Employee Project Details</h1>
      <div className='EmployeeTable'>
        <h2>Employees Details</h2>
        <table>
          <thead>
            <tr>
              <th>Employee Name</th>
              <th>Email</th>
              <th>Status</th>
              <th>Projects</th>
            </tr>
          </thead>
          <tbody>
            {employeesData.map((employee, index) => (
              <tr key={index}>
                <td>{employee.name}</td>
                <td>{employee.email}</td>
                <td>{employee.status}</td>
                <td>{employee.projects}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Overview;
