import React, { useEffect, useState } from 'react';
import { Routes, Route, useNavigate } from 'react-router-dom';
import Overview from './components/Overview';
import Sidebar from './components/Sidebar';
import Project from './components/Project';
import Login from './components/Login';
import './App.css';

function App() {
  const [role, setRole] = useState(null);
  const navigate = useNavigate();  // useNavigate can be used here now

  useEffect(() => {
    // Check for token and role in localStorage
    const savedToken = sessionStorage.getItem('csrfToken');
    const savedRole = sessionStorage.getItem('role');
    if (savedToken && savedRole) {
      setRole(parseInt(savedRole));
    }
  }, []);

  useEffect(() => {
    if (role === 4) {
      // Redirect Project Managers directly to the project section
      navigate('/Project');
    } else if (role === 5) {
      // Redirect Employees directly to the overview section
      navigate('/');
    }
    // Admin (role 2) will not have forced redirection and can freely navigate
  }, [role, navigate]);

  const handleLogin = (role) => {
    setRole(role);
  };

  // const handleLogout = () => {
  //   // localStorage.removeItem('token');
  //   // localStorage.removeItem('role');
  //   sessionStorage.removeItem('csrfToken')
  //   setRole(null);
  //   navigate('/');  // Redirect to login page on logout
  // };
  const handleLogout = async () => {
    try {
      // Clear JWT token and CSRF token stored in cookies
      await axios.post('http://localhost:5002/logout', {}, { withCredentials: true });

      // Clear CSRF token from session storage
      sessionStorage.removeItem('csrfToken'); 

      // Clear the role from state and sessionStorage (if needed)
      setRole(null); 

      // Redirect user to the login page
      navigate('/'); 

    } catch (error) {
      console.error('Error during logout:', error);
    }
  };

  if (!role) {
    return <Login onLogin={handleLogin} />;
  }

  return (
    <div className="App">
      <Sidebar role={role} onLogout={handleLogout} />
      <Routes>
        {role === 2 && <Route path="/" element={<Overview />} />} {/* Admin */}
        {role === 2 && <Route path="/Project" element={<Project />} />} {/* Admin */}
        {role === 4 && <Route path="/Project" element={<Project />} />} {/* Project Manager */}
        {role === 5 && <Route path="/" element={<Overview />} />} {/* Employee */}
      </Routes>
    </div>
  );
}

export default App;
