import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Routes, Route, useNavigate } from 'react-router-dom';
import Overview from './components/Overview';
import Sidebar from './components/Sidebar';
import Project from './components/Project';
import Login from './components/Login';
import './App.css';

function App() {
  const [role, setRole] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    // Check for token and role in localStorage
    const savedToken = localStorage.getItem('token');
    const savedRole = localStorage.getItem('role');
    if (savedToken && savedRole) {
      setRole(parseInt(savedRole));
    }
  }, []);

  const handleLogin = (role) => {
    setRole(role);
    localStorage.setItem('role', role); // Save role in localStorage
    // Redirect based on role after login
    if (role === 2) {
      navigate('/'); // Admin goes to overview
    } else if (role === 4) {
      navigate('/Project'); // Manager goes to project
    } else if (role === 5) {
      navigate('/'); // Employee goes to overview
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('role');
    setRole(null);
    navigate('/login'); // Redirect to login after logout
  };

  if (!role) {
    return <Login onLogin={handleLogin} />;
  }

  return (
    <div className="App">
      <Sidebar role={role} onLogout={handleLogout} />
      <Routes>
        {role === 2 && <Route path="/" element={<Overview />} />}
        {role === 2 && <Route path="/Project" element={<Project />} />}
        {role === 4 && <Route path="/Project" element={<Project />} />}
        {role === 5 && <Route path="/" element={<Overview />} />}
      </Routes>
    </div>
  );
}

export default App;
