import React, { useEffect, useState } from 'react';
import { Routes, Route, useNavigate } from 'react-router-dom';
import Overview from './components/Overview';
import Sidebar from './components/Sidebar';
import Project from './components/Project';
import Login from './components/Login';
import './App.css';

function App() {
  const [role, setRole] = useState(null);
  const navigate = useNavigate();  // useNavigate can be used here now

  useEffect(() => {
    // Check for token and role in localStorage
    const savedToken = localStorage.getItem('token');
    const savedRole = localStorage.getItem('role');
    if (savedToken && savedRole) {
      setRole(parseInt(savedRole));
    }
  }, []);

  useEffect(() => {
    if (role) {
      // Redirect based on role
      if (role === 4) {
        navigate('/Project');  // Project Manager to Project Overview
      } else if (role === 5) {
        navigate('/');  // Employee to Overview
      }
    }
  }, [role, navigate]);

  const handleLogin = (role) => {
    setRole(role);
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('role');
    setRole(null);
    navigate('/');  // Redirect to login page on logout
  };

  if (!role) {
    return <Login onLogin={handleLogin} />;
  }

  return (
    <div className="App">
      <Sidebar role={role} onLogout={handleLogout} />
      <Routes>
        {role === 2 && <Route path="/" element={<Overview />} />}
        {role === 2 && <Route path="/Project" element={<Project />} />}
        {role === 4 && <Route path="/Project" element={<Project />} />}
        {role === 5 && <Route path="/" element={<Overview />} />}
      </Routes>
    </div>
  );
}

export default App;
