import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { PieChart, Pie, Cell, Tooltip, Legend, ResponsiveContainer } from 'recharts';

const COLORS = ['#0088FE', '#00C49F', '#FFBB28'];

function Overview() {
  const [data, setData] = useState([]);
  const [totalEmployees, setTotalEmployees] = useState(0);
  const [filter, setFilter] = useState('day'); // default filter

  useEffect(() => {
    fetchData();
  }, [filter]);

  const fetchData = async () => {
    try {
      const response = await axios.get('http://localhost:5001/employees');
      const employees = response.data;

      setTotalEmployees([...new Set(employees.map(emp => emp.emp_id))].length); // calculate total employees

      const statusCounts = {
        Approved: 0,
        Unapproved: 0,
        Pending: 0,
      };

      employees.forEach(emp => {
        if (emp.status === 'Approved') statusCounts.Approved += 1;
        if (emp.status === 'Unapproved') statusCounts.Unapproved += 1;
        if (emp.status === 'Pending') statusCounts.Pending += 1;
      });

      setData([
        { name: 'Approved', value: statusCounts.Approved },
        { name: 'Unapproved', value: statusCounts.Unapproved },
        { name: 'Pending', value: statusCounts.Pending },
      ]);
    } catch (error) {
      console.error('Error fetching data', error);
    }
  };

  const handleFilterChange = (event) => {
    setFilter(event.target.value);
  };

  return (
    <div className="Overview">
      <h1>Overview</h1>
      <p>Total Employees: {totalEmployees}</p>
      <label htmlFor="filter">Filter by: </label>
      <select id="filter" value={filter} onChange={handleFilterChange}>
        <option value="day">Day</option>
        <option value="week">Week</option>
        <option value="month">Month</option>
      </select>
      <ResponsiveContainer width="100%" height={400}>
        <PieChart>
          <Pie data={data} dataKey="value" nameKey="name" cx="50%" cy="50%" outerRadius={150} fill="#8884d8" label>
            {data.map((entry, index) => (
              <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
            ))}
          </Pie>
          <Tooltip />
          <Legend />
        </PieChart>
      </ResponsiveContainer>
    </div>
  );
}

export default Overview;
